<div class="navclue abu-abu">	<div class="clue"><a href="#">Form Setting TPB Khusus</a></div></div><script>	$(document).ready(function() {		var kodeskpd = '<?php echo $this->data['id'];?>'; 		$("#optskpd").change(function(){			window.location = '?pg=tpbsetting&mode=tpbkhusussetting&kodeskpd='+$(this).val();		});				$("#tambah").click(function(){			if(kodeskpd == ''){				alert('Pilih SKPD terlebih dahulu!');				return false;				}			$("#fbulan").val(<?php echo date('m'); ?>);			$("#ftahun").val(<?php echo date('Y'); ?>);			$("#fnip").val('');			$("#periode").attr('target','');		    $("#periode").attr('action','?pg=tpbsetting&mode=tpbkhusussetting&kodeskpd=<?php echo $this->data['id'];?>');			$("#periode").submit();		});	});		function edit(bulan,tahun) {		$("#periode").attr('target','');		$("#periode").attr('action','?pg=tpbsetting&mode=tpbkhusussetting&kodeskpd=<?php echo $this->data['id'];?>');		$("#fbulan").val(bulan);		$("#ftahun").val(tahun);		$("#periode").submit();		}	function tpbkhusus(bulan,tahun) {		$("#periode").attr('target','');		$("#periode").attr('action','?pg=datatpb&mode=tpbkhusus&kodeskpd=<?php echo $this->data['id'];?>');		$("#fbulan").val(bulan);		$("#ftahun").val(tahun);		$("#periode").submit();		}</script><form id="periode" method="post" target=""><input type="hidden" id="fbulan" name="bulan"><input type="hidden" id="ftahun" name="tahun"><input type="hidden" id="fnip" name="nip"></form><div class="canvas-content">	<div class="ket">    	<table class="tabket">        	<tr><td class="td1">SKPD</td><td>: <?php echo "".$this->optSKPDKhusus($this->user['kodeskpd']).""; ?></td></tr>        </table>    </div>    <div class="nav-table"><div class="ll"><a href="#" class="aksi" alt="tambah" id="tambah"><div class="button biru">Tambah</div></a></div></div>    <table class="tabdata">    	<tr><th class="biru" width="30px">No.</th>            <th class="biru">Bulan</th>            <th class="biru">Tahun</th>            <th class="biru">Direktur</th>            <th class="biru">Kepala SDM</th>            <th class="biru">Petugas</th>                           <th class="biru" width="150px">&nbsp;</th>        </tr>        <?php		if($this->data['id']<>'') {			if($this->db->numRows($this->res) > 0){				$no = 1;				while($data = $this->db->fetchArray($this->res)){						echo '<tr>							<td class="td1">'.$no.'</td>							<td>'.$this->util->longMonth[$data['bulan']].'</td>							<td>'.$data['tahun'].'</td>							<td>'.$data['nama_direktur'].'</td>							<td>'.$data['nama_kepala_sdm'].'</td>							<td>'.$data['nama_petugas'].'</td>							<td class="td1" width="20%">							<div class="nav-table">							<div class="ll">            						<a href="#" class="aksi" alt="edit" onClick="edit(\''.$data['bulan'].'\',\''.$data['tahun'].'\');"><div class="button biru">Edit</div></a>            						<a href="#" class="aksi" alt="edit" onClick="tpbkhusus(\''.$data['bulan'].'\',\''.$data['tahun'].'\');"><div class="button biru">TPB Khusus</div></a>        						</div>        						</div>							</td>						  </tr>';					$no++;				}			}else{				echo '<tr><td class="td1" colspan="7">Data Kosong</td></tr>';				}		}else{			echo '<tr><td class="td1" colspan="7">Pilih SKPD terlebih dahulu</td></tr>';			}		?>    </table></div>